+* {
+  margin: 0;
+  padding: 0;
+  box-sizing: border-box;
+}
+
+body {
+  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
+  background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
+  color: #eee;
+  min-height: 100vh;
+  overflow-x: hidden;
+}
+
+.App {
+  display: flex;
+  min-height: 100vh;
+}
+
+/* ========== SIDEBAR ========== */
+.sidebar {
+  width: 280px;
+  background: rgba(22, 33, 62, 0.95);
+  backdrop-filter: blur(20px);
+  border-right: 2px solid rgba(0, 217, 255, 0.2);
+  display: flex;
+  flex-direction: column;
+  position: fixed;
+  left: 0;
+  top: 0;
+  height: 100vh;
+  transition: transform 0.3s ease;
+  z-index: 1000;
+  box-shadow: 4px 0 20px rgba(0, 0, 0, 0.5);
+}
+.sidebar.closed {
+  transform: translateX(-280px);
+}
+
+.sidebar-toggle {
+  position: fixed;
+  top: 20px;
+  width: 40px;
+  height: 40px;
+  background: rgba(0, 217, 255, 0.2);
+  border: 2px solid #00d9ff;
+  color: #00d9ff;
+  border-radius: 50%;
+  cursor: pointer;
+  transition: all 0.3s ease;
+  z-index: 1001;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  font-size: 1.2rem;
+}
+
+/* Posición cuando sidebar está abierto */
+.sidebar.open + .sidebar-toggle,
+.sidebar:not(.closed) + .sidebar-toggle {
+  left: 280px;
+}
+
+/* Posición cuando sidebar está cerrado OJO!! */
+.sidebar.closed + .sidebar-toggle {
+  left: 20px;
+}
+
+.sidebar-toggle:hover {
+  background: rgba(0, 217, 255, 0.3);
+  transform: scale(1.1);
+}
+
+.sidebar-toggle:hover {
+  background: rgba(0, 217, 255, 0.3);
+  transform: scale(1.1);
+}
+
+.sidebar-header {
+  padding: 2rem 1.5rem;
+  border-bottom: 2px solid rgba(0, 217, 255, 0.2);
+}
+
+.sidebar-header h2 {
+  color: #00d9ff;
+  font-size: 1.5rem;
+  text-shadow: 0 0 10px rgba(0, 217, 255, 0.5);
+}
+
+.sidebar-nav {
+  flex: 1;
+  padding: 1rem 0;
+  overflow-y: auto;
+}
+
+.sidebar-item {
+  width: 100%;
+  padding: 1.2rem 1.5rem;
+  background: transparent;
+  border: none;
+  color: #aaa;
+  cursor: pointer;
+  display: flex;
+  align-items: center;
+  gap: 1rem;
+  transition: all 0.3s ease;
+  font-size: 1.05rem;
+  text-align: left;
+  border-left: 3px solid transparent;
+  margin: 0.2rem 0;
+}
+
+.sidebar-item:hover {
+  background: rgba(0, 217, 255, 0.1);
+  color: #00d9ff;
+}
+
+.sidebar-item.active {
+  background: rgba(0, 217, 255, 0.2);
+  color: #00d9ff;
+  border-left-color: #00d9ff;
+  font-weight: bold;
+}
+
+.sidebar-icon {
+  font-size: 1.6rem;
+  width: 35px;
+}
+
+.sidebar-label {
+  flex: 1;
+}
+
+.sidebar-footer {
+  padding: 1rem;
+  text-align: center;
+  border-top: 2px solid rgba(0, 217, 255, 0.2);
+  color: #666;
+  font-size: 0.85rem;
+}
+
+/* ========== MAIN CONTENT - CORREGIDO ========== */
+.main-content {
+  flex: 1;
+  margin-left: 280px;
+  transition: margin-left 0.3s ease;
+  min-height: 100vh;
+  width: calc(100% - 280px);
+}
+
+/* Cuando el sidebar está cerrado */
+.App.sidebar-closed .main-content {
+  margin-left: 0 !important;
+  width: 100% !important;
+}
+
+.main-header {
+  background: rgba(22, 33, 62, 0.9);
+  backdrop-filter: blur(10px);
+  padding: 2rem;
+  text-align: center;
+  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.5);
+  border-bottom: 2px solid #00d9ff;
+  position: sticky;
+  top: 0;
+  z-index: 100;
+}
+
+.main-header h1 {
+  color: #00d9ff;
+  font-size: 2.5rem;
+  margin-bottom: 0.5rem;
+  text-shadow: 0 0 20px rgba(0, 217, 255, 0.5);
+}
+
+.main-header p {
+  color: #aaa;
+  font-size: 1rem;
+}
+
+/* CORREGIDO: Content area sin max-width restrictivo */
+.content-area {
+  padding: 2rem 3rem;
+  width: 100%;
+}
+
+/* ========== HERO CAROUSEL - MEJORADO Y CENTRADO ========== */
+.hero-carousel {
+  position: relative;
+  width: 100%;
+  max-width: 1400px;
+  height: 500px;
+  margin: 0 auto 3rem;
+  border-radius: 20px;
+  overflow: hidden;
+  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.5);
+}
+
+.carousel-container {
+  position: relative;
+  width: 100%;
+  height: 100%;
+}
+
+.carousel-slide {
+  position: absolute;
+  width: 100%;
+  height: 100%;
+  opacity: 0;
+  transition: opacity 1s ease-in-out;
+  background-size: cover;
+  background-position: center;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+}
+
+.carousel-slide.active {
+  opacity: 1;
+  z-index: 1;
+}
+
+.carousel-content {
+  text-align: center;
+  max-width: 800px;
+  padding: 2rem;
+  z-index: 2;
+}
+
+.carousel-title {
+  font-size: 3.5rem;
+  color: #00d9ff;
+  margin-bottom: 1rem;
+  text-shadow: 0 4px 20px rgba(0, 0, 0, 0.8);
+  font-weight: bold;
+  animation: fadeInUp 0.8s ease;
+}
+
+.carousel-description {
+  font-size: 1.5rem;
+  color: #eee;
+  margin-bottom: 2rem;
+  text-shadow: 0 2px 10px rgba(0, 0, 0, 0.8);
+  line-height: 1.6;
+  animation: fadeInUp 1s ease;
+}
+
+.carousel-button {
+  padding: 1rem 2.5rem;
+  font-size: 1.1rem;
+  background: linear-gradient(135deg, #00d9ff 0%, #0099cc 100%);
+  border: none;
+  border-radius: 50px;
+  color: #fff;
+  font-weight: bold;
+  cursor: pointer;
+  transition: all 0.3s ease;
+  box-shadow: 0 4px 15px rgba(0, 217, 255, 0.4);
+  animation: fadeInUp 1.2s ease;
+}
+
+.carousel-button:hover {
+  transform: translateY(-3px);
+  box-shadow: 0 6px 25px rgba(0, 217, 255, 0.6);
+  background: linear-gradient(135deg, #0099cc 0%, #00d9ff 100%);
+}
+
+@keyframes fadeInUp {
+  from {
+    opacity: 0;
+    transform: translateY(30px);
+  }
+  to {
+    opacity: 1;
+    transform: translateY(0);
+  }
+}
+
+/* Botones de navegación */
+.carousel-nav {
+  position: absolute;
+  top: 50%;
+  transform: translateY(-50%);
+  background: rgba(0, 217, 255, 0.3);
+  backdrop-filter: blur(10px);
+  border: 2px solid #00d9ff;
+  color: #00d9ff;
+  width: 60px;
+  height: 60px;
+  border-radius: 50%;
+  cursor: pointer;
+  font-size: 2rem;
+  transition: all 0.3s ease;
+  z-index: 10;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+}
+
+.carousel-nav:hover {
+  background: rgba(0, 217, 255, 0.5);
+  transform: translateY(-50%) scale(1.1);
+}
+
+.carousel-nav.prev {
+  left: 2rem;
+}
+
+.carousel-nav.next {
+  right: 2rem;
+}
+
+/* Indicadores */
+.carousel-indicators {
+  position: absolute;
+  bottom: 2rem;
+  left: 50%;
+  transform: translateX(-50%);
+  display: flex;
+  gap: 1rem;
+  z-index: 10;
+}
+
+.indicator {
+  width: 12px;
+  height: 12px;
+  border-radius: 50%;
+  background: rgba(255, 255, 255, 0.3);
+  border: 2px solid #00d9ff;
+  cursor: pointer;
+  transition: all 0.3s ease;
+}
+
+.indicator.active {
+  background: #00d9ff;
+  width: 40px;
+  border-radius: 10px;
+  box-shadow: 0 0 15px rgba(0, 217, 255, 0.8);
+}
+
+.indicator:hover:not(.active) {
+  background: rgba(0, 217, 255, 0.5);
+}
+
+/* ========== SEARCH BAR - MEJORADA Y CENTRADA ========== */
+.search-bar {
+  margin-bottom: 2rem;
+}
+
+.search-input-wrapper {
+  position: relative;
+  max-width: 700px;
+  margin: 0 auto;
+}
+
+.search-icon {
+  position: absolute;
+  left: 1.2rem;
+  top: 50%;
+  transform: translateY(-50%);
+  font-size: 1.3rem;
+  color: #00d9ff;
+}
+
+.search-input {
+  width: 100%;
+  padding: 1.2rem 3.5rem;
+  background: rgba(22, 33, 62, 0.8);
+  border: 2px solid rgba(0, 217, 255, 0.3);
+  border-radius: 50px;
+  color: #eee;
+  font-size: 1.1rem;
+  transition: all 0.3s ease;
+}
+
+.search-input:focus {
+  outline: none;
+  border-color: #00d9ff;
+  box-shadow: 0 0 20px rgba(0, 217, 255, 0.3);
+}
+
+.search-input::placeholder {
+  color: #666;
+}
+
+.clear-search {
+  position: absolute;
+  right: 1rem;
+  top: 50%;
+  transform: translateY(-50%);
+  background: rgba(255, 107, 107, 0.2);
+  border: none;
+  color: #ff6b6b;
+  width: 35px;
+  height: 35px;
+  border-radius: 50%;
+  cursor: pointer;
+  transition: all 0.3s ease;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+}
+
+.clear-search:hover {
+  background: rgba(255, 107, 107, 0.3);
+  transform: translateY(-50%) rotate(90deg);
+}
+
+/* ========== VIEW TITLE ========== */
+.view-title {
+  color: #00d9ff;
+  font-size: 2rem;
+  margin-bottom: 2rem;
+  display: flex;
+  align-items: center;
+  gap: 1rem;
+}
+
+.game-count {
+  font-size: 1rem;
+  color: #aaa;
+  font-weight: normal;
+}
+
+/* ========== EMPTY STATE ========== */
+.empty-state {
+  text-align: center;
+  padding: 4rem 2rem;
+  color: #aaa;
+}
+
+.empty-state p {
+  font-size: 1.2rem;
+  margin: 0.5rem 0;
+}
+
+.empty-state p:first-child {
+  font-size: 1.5rem;
+  color: #00d9ff;
+}
+
+/* ========== GAME LIST - MEJORADA ========== */
+.games-grid {
+  display: grid;
+  grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
+  gap: 2rem;
