+  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
+  border: 1px solid rgba(0, 217, 255, 0.2);
+  position: relative;
+}
+
+.game-card:hover {
+  transform: translateY(-8px);
+  box-shadow: 0 8px 25px rgba(0, 217, 255, 0.4);
+  border-color: #00d9ff;
+}
+
+.favorite-btn {
